  title: string;
  href: string;
  icon: React.ReactNode;
  permissions?: string[];
};

type NavSection = {
  id: string;
  title: string;
  icon: React.ReactNode;
  defaultOpen?: boolean;
  items: NavItem[];
  permissions?: string[];
  adminOnly?: boolean;
