import React from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { AppLayout } from "@/components/layout/app-layout";
import { useAuth } from "@/hooks/use-auth";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { 
  Users, 
  Loader2, 
  Save, 
  AlertTriangle,
  Phone,
  ExternalLink,
  Shield,
  Mail
} from "lucide-react";
import { motion } from "framer-motion";

// Employee limits schema
const employeeLimitsSchema = z.object({
  systemLimits: z.object({
    maxEmployees: z.number().min(1).max(1000),
    contactEmail: z.string().email(),
    contactPhone: z.string(),
    upgradeLink: z.string().url()
  })
});

type EmployeeLimitsForm = z.infer<typeof employeeLimitsSchema>;

export default function EmployeeLimitPage() {
  const { toast } = useToast();
  const { user } = useAuth();

  // Check if user is developer
  if (user?.role !== 'developer') {
    return (
      <AppLayout>
        <div className="min-h-screen flex items-center justify-center">
          <Card className="max-w-md mx-auto">
            <CardHeader>
              <CardTitle className="flex items-center text-red-600">
                <Shield className="w-5 h-5 mr-2" />
                Access Denied
              </CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-slate-600">
                This area is restricted to developer accounts only.
              </p>
            </CardContent>
          </Card>
        </div>
      </AppLayout>
    );
  }

  // Fetch system settings for employee limits
  const { data: systemSettings, isLoading: isLoadingSettings } = useQuery({
    queryKey: ["/api/settings/system"],
    queryFn: async () => {
      const res = await apiRequest("GET", "/api/settings/system");
      return await res.json();
    }
  });

  // Fetch all employees for count
  const { data: employees = [] } = useQuery<any[]>({
    queryKey: ["/api/employees"],
  });

  const form = useForm<EmployeeLimitsForm>({
    resolver: zodResolver(employeeLimitsSchema),
    defaultValues: {
      systemLimits: systemSettings?.systemLimits || {
        maxEmployees: 10,
        contactEmail: "",
        contactPhone: "",
        upgradeLink: ""
      }
    },
  });

  // Update form when data loads
  React.useEffect(() => {
    if (systemSettings?.systemLimits) {
      form.reset({ systemLimits: systemSettings.systemLimits });
    }
  }, [systemSettings, form]);

  // Save employee limits mutation
  const saveLimitsMutation = useMutation({
    mutationFn: async (data: EmployeeLimitsForm) => {
      const fullSettings = { ...systemSettings, ...data };
      const res = await apiRequest("PUT", "/api/settings/system", fullSettings);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settings/system"] });
      toast({
        title: "Limits updated",
        description: "Employee limits have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: `Failed to update limits: ${error.message}`,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: EmployeeLimitsForm) => {
    saveLimitsMutation.mutate(data);
  };

  if (isLoadingSettings) {
    return (
      <AppLayout>
        <div className="min-h-screen flex items-center justify-center">
          <Loader2 className="h-8 w-8 animate-spin" />
        </div>
      </AppLayout>
    );
  }

  const currentEmployeeCount = employees.length;
  const maxEmployees = systemSettings?.systemLimits?.maxEmployees || 10;
  const isNearLimit = currentEmployeeCount >= maxEmployees * 0.8;
  const isAtLimit = currentEmployeeCount >= maxEmployees;

  return (
    <AppLayout autoHeight>
      <div className="bg-gradient-to-br from-slate-50 via-white to-slate-50">
        {/* Header */}
        <div className="bg-gradient-to-r from-blue-900 via-blue-800 to-blue-900 relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-blue-600/20 via-purple-600/20 to-blue-600/20"></div>
          <div className="relative px-6 py-12">
            <div className="max-w-7xl mx-auto">
              <motion.div 
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6 }}
                className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-6"
              >
                <div className="flex items-center space-x-6">
                  <div className="bg-gradient-to-br from-blue-500 to-purple-600 p-4 rounded-2xl shadow-xl">
                    <Users className="w-8 h-8 text-white" />
                  </div>
                  <div>
                    <h1 className="text-4xl font-bold bg-gradient-to-r from-white to-slate-200 bg-clip-text text-transparent mb-2">
                      Employee Limits
                    </h1>
                    <p className="text-slate-300 text-lg max-w-2xl">
                      Manage employee capacity and upgrade options
                    </p>
                  </div>
                </div>
              </motion.div>
            </div>
          </div>
        </div>

        <div className="max-w-7xl mx-auto px-6 py-8">
          {/* Employee Limit Status */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.1 }}
            className="mb-8"
          >
            <Card className={`border-2 ${isAtLimit ? 'border-red-200 bg-red-50' : isNearLimit ? 'border-orange-200 bg-orange-50' : 'border-green-200 bg-green-50'}`}>
              <CardHeader>
                <CardTitle className={`flex items-center ${isAtLimit ? 'text-red-700' : isNearLimit ? 'text-orange-700' : 'text-green-700'}`}>
                  <Users className="w-5 h-5 mr-2" />
                  Current Status
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="flex items-center justify-between">
                  <div>
                    <p className={`text-2xl font-bold ${isAtLimit ? 'text-red-700' : isNearLimit ? 'text-orange-700' : 'text-green-700'}`} data-testid="text-employee-count">
                      {currentEmployeeCount} / {maxEmployees}
                    </p>
                    <p className="text-slate-600">Employees registered</p>
                  </div>
                  {(isAtLimit || isNearLimit) && (
                    <div className="flex items-center space-x-2">
                      <AlertTriangle className={`w-5 h-5 ${isAtLimit ? 'text-red-600' : 'text-orange-600'}`} />
                      <span className={`font-medium ${isAtLimit ? 'text-red-700' : 'text-orange-700'}`} data-testid="text-limit-status">
                        {isAtLimit ? 'Limit Reached' : 'Approaching Limit'}
                      </span>
                    </div>
                  )}
                </div>
                <div className={`mt-4 w-full bg-gray-200 rounded-full h-2`}>
                  <div 
                    className={`h-2 rounded-full transition-all duration-300 ${isAtLimit ? 'bg-red-500' : isNearLimit ? 'bg-orange-500' : 'bg-green-500'}`}
                    style={{ width: `${Math.min((currentEmployeeCount / maxEmployees) * 100, 100)}%` }}
                  ></div>
                </div>
              </CardContent>
            </Card>
          </motion.div>

          {/* Limits Configuration Panel */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <Card className="shadow-xl border-2 border-slate-200">
              <CardHeader>
                <CardTitle>Employee Limit Configuration</CardTitle>
                <CardDescription>
                  Configure maximum employee capacity and upgrade information
                </CardDescription>
              </CardHeader>
              <CardContent className="p-6">
                <Form {...form}>
                  <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                    <div>
                      <h3 className="text-lg font-semibold mb-4">Capacity Settings</h3>
                      <FormField
                        control={form.control}
                        name="systemLimits.maxEmployees"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel className="flex items-center">
                              <Users className="w-4 h-4 mr-2" />
                              Maximum Employees
                            </FormLabel>
                            <FormControl>
                              <Input 
                                {...field} 
                                type="number" 
                                min="1" 
                                max="1000"
                                onChange={(e) => field.onChange(parseInt(e.target.value))}
                                data-testid="input-max-employees"
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div>
                      <h3 className="text-lg font-semibold mb-4">Support & Upgrade Information</h3>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="systemLimits.contactEmail"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="flex items-center">
                                <Mail className="w-4 h-4 mr-2" />
                                Support Email
                              </FormLabel>
                              <FormControl>
                                <Input {...field} type="email" data-testid="input-support-email" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="systemLimits.contactPhone"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel className="flex items-center">
                                <Phone className="w-4 h-4 mr-2" />
                                Support Phone
                              </FormLabel>
                              <FormControl>
                                <Input {...field} type="tel" data-testid="input-support-phone" />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                      <FormField
                        control={form.control}
                        name="systemLimits.upgradeLink"
                        render={({ field }) => (
                          <FormItem className="mt-4">
                            <FormLabel className="flex items-center">
                              <ExternalLink className="w-4 h-4 mr-2" />
                              Upgrade URL
                            </FormLabel>
                            <FormControl>
                              <Input {...field} type="url" data-testid="input-upgrade-url" />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>

                    <div className="pt-6 border-t">
                      <Button 
                        type="submit" 
                        disabled={saveLimitsMutation.isPending}
                        className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700"
                        data-testid="button-save-limits"
                      >
                        {saveLimitsMutation.isPending ? (
                          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                        ) : (
                          <Save className="w-4 h-4 mr-2" />
                        )}
                        Update Limits
                      </Button>
                    </div>
                  </form>
                </Form>
              </CardContent>
            </Card>
          </motion.div>
        </div>
      </div>
    </AppLayout>
  );
}

client/src/components/employees/multi-st.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { motion, AnimatePresence } from "framer-motion";
import { 
  Form, 
  FormControl, 
  FormField, 
  FormItem, 
  FormLabel, 
  FormMessage 
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { User, Department } from "@shared/schema";
import { Loader2, ChevronLeft, ChevronRight, CalendarIcon, User as UserIcon, Building2, CreditCard, Upload, X } from "lucide-react";
import { format, differenceInYears, subYears } from "date-fns";
import { cn } from "@/lib/utils";
import { ContactPopup } from "@/components/ui/contact-popup";

interface MultiStepEmployeeFormProps {
  employee?: User;
  departments: Department[];
  onSuccess: () => void;
}

export function MultiStepEmployeeForm({ employee, departments, onSuccess }: MultiStepEmployeeFormProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const isEditing = !!employee;
  const [currentStep, setCurrentStep] = useState(1);
  const totalSteps = 3;
  const [selectedPhoto, setSelectedPhoto] = useState<string | null>(employee?.photoUrl || null);
  const [showContactPopup, setShowContactPopup] = useState(false);
  const [employeeLimitError, setEmployeeLimitError] = useState<any>(null);
  // Fetch employees for reporting manager dropdown
  const { data: employees = [] } = useQuery({
    queryKey: ['employees'],
    queryFn: async () => {
      try {
        const response = await apiRequest("GET", "/api/employees");
        const data = await response.json();
        return data as User[];
      } catch (error) {
        console.error("Failed to fetch employees:", error);
        return [];
      }
    },
  });

  // Create dynamic form schema based on editing state
  const formSchema = z.object({
    // Personal Information (Step 1)
    employeeId: z.string().optional(),
    firstName: z.string().min(1, "First name is required"),
    lastName: z.string().min(1, "Last name is required"),
    email: z.string().email("Invalid email address"),
    phoneNumber: z.string().optional(),
    address: z.string().optional(),
    dateOfBirth: z.date().optional().refine((date) => {
      if (!date) return true; // Optional field
      const age = differenceInYears(new Date(), date);
      return age >= 20;
    }, {
      message: "Age must be more than 20"
    }),
    gender: z.enum(['male', 'female', 'other', 'prefer_not_to_say']).optional(),
    maritalStatus: z.enum(['single', 'married', 'divorced', 'widowed', 'prefer_not_to_say']).optional(),
    photoUrl: z.string().optional(),

    // Company Details (Step 2)
    username: z.string().min(3, "Username must be at least 3 characters"),
    password: isEditing 
      ? z.string().optional()
      : z.string().min(6, "Password must be at least 6 characters"),
    role: z.enum(['admin', 'hr', 'manager', 'employee', 'developer']),
    departmentId: z.number().nullable(),
    position: z.string().optional(),
    joinDate: z.date().optional(),
    workLocation: z.string().optional(),
    reportingTo: z.number().nullable(),
    salary: z.number().min(1, "Salary must be greater than 0").optional(),

    // Bank Information (Step 3)
    bankAccountNumber: z.string().optional(),
    bankAccountHolderName: z.string().optional(),
    bankName: z.string().optional(),
    bankIFSCCode: z.string().optional(),
    bankAccountType: z.enum(['savings', 'current', 'salary']).optional(),
    aadhaarCard: z.string().optional(),
    panCard: z.string().optional(),
  });

  type FormValues = z.infer<typeof formSchema>;

  const form = useForm<FormValues>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      employeeId: employee?.id?.toString() || "",
      firstName: employee?.firstName || "",
      lastName: employee?.lastName || "",
      email: employee?.email || "",
      phoneNumber: employee?.phoneNumber || "",
      address: employee?.address || "",
      dateOfBirth: employee?.dateOfBirth ? new Date(employee.dateOfBirth) : undefined,
      gender: employee?.gender || undefined,
      maritalStatus: employee?.maritalStatus || undefined,
      photoUrl: employee?.photoUrl || "",
      username: employee?.username || "",
      password: isEditing ? "" : "",
      role: employee?.role || "employee",
      departmentId: employee?.departmentId || null,
      position: employee?.position || "",
      joinDate: employee?.joinDate ? new Date(employee.joinDate) : undefined,
      workLocation: employee?.workLocation || "",
      reportingTo: employee?.reportingTo || null,
      salary: employee?.salary || undefined,
      bankAccountNumber: employee?.bankAccountNumber || "",
      bankAccountHolderName: employee?.bankAccountHolderName || "",
      bankName: employee?.bankName || "",
      bankIFSCCode: employee?.bankIFSCCode || "",
      bankAccountType: employee?.bankAccountType || undefined,
      aadhaarCard: employee?.aadhaarCard || "",
      panCard: employee?.panCard || "",
    },
  });

  // Calculate age from date of birth
  const calculateAge = (birthDate: Date | undefined): number | null => {
    if (!birthDate) return null;
    return differenceInYears(new Date(), birthDate);
  };

  // Watch the date of birth to calculate age
  const dateOfBirth = form.watch("dateOfBirth");
  const age = calculateAge(dateOfBirth);

  // Handle photo file selection and conversion to base64
  const handlePhotoChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        toast({
          title: "Invalid file type",
          description: "Please select an image file (JPG, PNG, GIF)",
          variant: "destructive",
        });
        return;
      }

      // Validate file size (max 2MB)
      if (file.size > 2 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select an image smaller than 2MB",
          variant: "destructive",
        });
        return;
      }

      const reader = new FileReader();
      reader.onload = () => {
        const base64 = reader.result as string;
        setSelectedPhoto(base64);
        form.setValue('photoUrl', base64);
      };
      reader.readAsDataURL(file);
    }
  };

  const removePhoto = () => {
    setSelectedPhoto(null);
    form.setValue('photoUrl', '');
  };

  // Create or update employee mutation
  const mutation = useMutation({
    mutationFn: async (values: FormValues) => {
      if (isEditing) {
        // For editing, remove password if it's empty
        if (!values.password) {
          const { password, ...dataWithoutPassword } = values;
          return await apiRequest(
            "PUT", 
            `/api/employees/${employee.id}`, 
            dataWithoutPassword
          );
        } else {
          return await apiRequest(
            "PUT", 
            `/api/employees/${employee.id}`, 
            values
          );
        }
      } else {
        return await apiRequest("POST", "/api/register", values);
      }
    },
    onSuccess: () => {
      toast({
        title: isEditing ? "Employee updated" : "Employee created",
        description: isEditing 
          ? "Employee information has been updated successfully." 
          : "New employee has been created successfully.",
      });
      // Invalidate employees query to refresh the list
      queryClient.invalidateQueries({ queryKey: ['employees'] });
      onSuccess();
    },
    onError: (error: any) => {

      // Check if this is an employee limit error (status 429)
      if (error.status === 429 || error.message?.includes('Employee limit reached')) {
        setEmployeeLimitError({
          currentCount: error.currentCount || 10,
          maxEmployees: error.maxEmployees || 10,
          contactInfo: error.contactInfo || {
            email: "support@hrconnect.com",
            phone: "+1-234-567-8900",
            upgradeLink: "https://hrconnect.com/upgrade"
          }
        });
        setShowContactPopup(true);
        return;
      }

      // Default error handling
      toast({
        title: "Error",
        description: error.message || "An error occurred while creating the employee",
        variant: "destructive",
      });
    },
  });

  const nextStep = async () => {
    const fieldsToValidate = getFieldsForStep(currentStep);
    const isValid = await form.trigger(fieldsToValidate);

    if (isValid && currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
      // Smooth scroll to top when moving to next step
      setTimeout(() => {
        const container = document.querySelector('[role="dialog"]') || 
                          document.querySelector('.form-container')?.closest('[data-radix-dialog-content]') ||
                          document.querySelector('[data-radix-dialog-content]');
        if (container) {
          container.scrollTo({ top: 0, behavior: 'smooth' });
        } else {
          // Fallback to window scroll
          window.scrollTo({ top: 0, behavior: 'smooth' });
        }
      }, 150);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
      // Smooth scroll to top when moving to previous step
      setTimeout(() => {
        const container = document.querySelector('[role="dialog"]') || 
                          document.querySelector('.form-container')?.closest('[data-radix-dialog-content]') ||
                          document.querySelector('[data-radix-dialog-content]');
        if (container) {
          container.scrollTo({ top: 0, behavior: 'smooth' });
        } else {
          // Fallback to window scroll
          window.scrollTo({ top: 0, behavior: 'smooth' });
        }
      }, 150);
    }
  };

  const onSubmit = (values: FormValues) => {
    // Only allow submission on the final step
    if (currentStep === totalSteps) {
      mutation.mutate(values);
    } else {
      // If not on final step, go to next step instead
      nextStep();
    }
  };

  const getFieldsForStep = (step: number): (keyof FormValues)[] => {
    switch (step) {
      case 1:
        return ['employeeId', 'firstName', 'lastName', 'email'];
      case 2:
        // For editing, password is optional, so don't validate it
        return isEditing ? ['username', 'role'] : ['username', 'password', 'role'];
      case 3:
        // Final step - validate all required fields for form submission
        return isEditing ? [] : [];
      default:
        return [];
    }
  };

  const stepVariants = {
    initial: { opacity: 0, x: 60, scale: 0.95 },
    in: { opacity: 1, x: 0, scale: 1 },
    out: { opacity: 0, x: -60, scale: 0.95 }
  };

  const stepTransition = {
    type: "spring",
    stiffness: 300,
    damping: 30,
    mass: 0.8
  };

  const steps = [
    { number: 1, title: "Personal Information", icon: UserIcon },
    { number: 2, title: "Company Details", icon: Building2 },
    { number: 3, title: "Bank Information", icon: CreditCard },
  ];

  return (
    <div className="form-container h-full max-h-[90vh] flex flex-col">
      {/* Compact Professional Header */}
      <div className="bg-white px-6 py-4 border-b border-slate-200">
        <div className="flex items-center justify-between mb-4">
          <div>
            <h2 className="text-xl font-semibold text-slate-900">
              {isEditing ? "Update Employee Profile" : "Create New Employee"}
            </h2>
            <p className="text-slate-600 text-sm">
              {isEditing 
                ? "Modify employee information and account settings" 
                : "Complete all steps to onboard a new team member"}
            </p>
          </div>
          <div className="text-right">
            <div className="text-sm font-medium text-slate-900">Step {currentStep} of {totalSteps}</div>
            <div className="text-xs text-slate-500">{steps[currentStep - 1]?.title}</div>
          </div>
        </div>

        {/* Simplified Progress Steps */}
        <div className="relative">
          <div className="flex items-center justify-between relative z-10">
            {steps.map((step, index) => (
              <div key={step.number} className="flex flex-col items-center">
                <div className="relative">
                  <div className={cn(
                    "flex items-center justify-center w-10 h-10 rounded-full border-2 transition-all duration-300",
                    currentStep >= step.number
                      ? "bg-teal-600 border-teal-600 text-white"
                      : currentStep === step.number
