      tasks.push({
        icon: <CheckCircle2 className="h-4 w-4 text-green-500" />,
        title: "Review leave requests",
        link: "/leave?filter=pending"
      });
    }

    return tasks;
  };

  const tasks = getRoleTasks();

  return (
    <motion.div 
      initial={{ opacity: 0, y: 20 }}
